require(['jquery'],function($) {
  var getExtAcceptedAction = function() {
    const canDoByExt = {};
    ['doc', 'docx', 'xls', 'xlsx', 'ppt', 'pptx', 'csv', 'rtf', 'txt', 'odt', 'ods', 'odp', 'odg'].forEach(function(x) {
      canDoByExt[x] = 'edit';
    });
    ['pdf', 'fb2'].forEach(function(x) {
      canDoByExt[x] = 'view';
    });
    return canDoByExt;
  };
  const canDoByExt = getExtAcceptedAction();

  var getTemplates = function(hasPagination) {
    const config = JSON.parse($('#collabora-config').text());
    const template = {};
    template.attachButton = hasPagination ? $(config.tableButtonTemplate) : $(config.listButtonTemplate);
    template.createButton = $(config.createButtonTemplate);
    template.newFileModal = $(config.newFileModal);
    return template;
  };

  var getAccessRights = function(fileName) {
    const fileType = fileName.slice(fileName.lastIndexOf('.') + 1).toLowerCase();
    let accessRights = canDoByExt[fileType];
    if (!accessRights) {
      return false;
    }
    if (!XWiki.hasEdit) {
      accessRights = 'view';
    }

    return accessRights;
  };

  var getActionURL = function(fileName, accessRights) {
    const actionDoc = new XWiki.Document(XWiki.Model.resolve('Collabora.Code.Main', XWiki.EntityType.DOCUMENT));
    const queryString = $.param({
      'document': XWiki.Document.currentDocumentReference.toString(),
      'filename': fileName,
      'action': accessRights,
      'xpage': 'plain'
    });

    return actionDoc.getURL('get', queryString);
  };

  var getCollaboraButton = function(thisButton, fileName, accessRights) {
    // The data translations attributes are used for updating the title from js, since it depends on the access rights.
    // This can be changed once licensing starts depending on a XWiki version >= 13.8 to include
    // XWIKI-18973: Simplify the way JavaScript code loads translation messages.
    const title = thisButton.data(accessRights == 'edit' ? 'editTitle' : 'viewTitle');
    thisButton.attr('title', title);
    thisButton.find('img').attr('alt', title);
    thisButton.removeAttr('data-edit-title');
    thisButton.removeAttr('data-view-title');

    thisButton.attr('href', getActionURL(fileName, accessRights));
    return thisButton;
  };

  var decorateWithCollaboraButtons = function() {
    // Attachments are displayed using a livetable since 14.6, and using a livedata from 14.8.
    const hasPagination = $("#_attachments table").length > 0;
    const templates = getTemplates(hasPagination);

    $("#_attachments span.name").each(function(i, attach) {
      const attachActions = hasPagination ? $(attach).closest('tr').find('.attachmentActions') : $(attach).siblings('.xwikibuttonlinks');
      if ($(attachActions).find('a.collaboraEdit').length) {
        return;
      }

      const fileName = $(attach).find('a').text().trim();
      const accessRights = getAccessRights(fileName);
      if (!accessRights) {
        return;
      }

      $(attachActions).append(getCollaboraButton(templates.attachButton.clone(), fileName, accessRights));
    });

    $('#attachmentscontent').append(templates.createButton);
    $('body').append(templates.newFileModal);
  };

  $(decorateWithCollaboraButtons);
  $(document).on('xwiki:livedata:afterEntryFetch', function (event) {
    if ($(event.target).attr('id') === 'docAttachments') {
      // We need to setTimeout(0) to allow the live data to display the entries before decorating them with the buttons.
      setTimeout(decorateWithCollaboraButtons, 0);
    }
  });
  $(document).on('xwiki:livetable:docAttachments:loadingComplete', decorateWithCollaboraButtons);
  $(document).on('xwiki:html5upload:fileFinished', decorateWithCollaboraButtons);
  $(document).on('xwiki:docextra:loaded', decorateWithCollaboraButtons);

  $(document).on('click', '#newCollaboraFileModal input[type=submit]', function(e) {
    e.preventDefault();
    const form = $(this).parents('.modal-content').find('form');
    const formData = new FormData(form[0]);
    const fileName = formData.get('fileName') + formData.get('fileType');
    const errorMessage = $(this).data('error');

    // If the file exists, display an eror message, otherwise create it and redirect to Collabora edit.
    const attachURL =
      new XWiki.Document(XWiki.Model.resolve(
        XWiki.Document.currentDocumentReference.toString(), XWiki.EntityType.DOCUMENT
      )).getRestURL() + "/attachments/" + encodeURIComponent(fileName);
    $.get(attachURL)
      .done(function () {
        new XWiki.widgets.Notification(errorMessage, "error");
      })
      .fail(function () {
        $.ajax({
          url: attachURL,
          type: "PUT",
        }).done(function () {
          window.location = getActionURL(fileName, getAccessRights(fileName, canDoByExt));
        });
      });
  });

  // Prefill the file extension depending on the file type selection.
  $(document).on('change', '#newCollaboraFileModal select[name=fileType]', function() {
    $('#newCollaboraFileModal input[name=fileExt]').val($(this).val());
  });

  // Disable the create button if no file name is given.
  $(document).on('keyup change', '#newCollaboraFileModal input[name=fileName]', function() {
    $("#newCollaboraFileModal input[type=submit]").prop('disabled', $(this).val() === '');
  });
});
